curl -s "https://api.github.com/repos/tldraw/tldraw/git/trees/main?recursive=1" | jq -r '.tree[] | select(.path | test("(?i)context.md$")) | .path' | while read -r path; do filename=$(echo "$path" | tr '/' '_'); echo "Saving: $filename"; curl -s "https://raw.githubusercontent.com/tldraw/tldraw/main/$path" > "$filename"; echo -e "\n\n# ==================\n# FILE: $path\n# ==================\n" >> tldraw_combined.md; cat "$filename" >> tldraw_combined.md; done; echo "DONE! Files downloaded separately and combined."
